import React , {useState} from 'react'
import { AiFillSave } from 'react-icons/ai'
import {EditorConvertToHTML} from '../../layouts/Editor'
import { EditorState, convertToRaw } from 'draft-js';
import { Editor } from 'react-draft-wysiwyg';
import draftToHtml from 'draftjs-to-html';
import htmlToDraft from 'html-to-draftjs';


export const AddArticle = () => {

    const [editorState , setEditorState] = useState( EditorState.createEmpty())
   
  
    const onEditorStateChange = (editorState) => {
      setEditorState(editorState)
    };
  
    return (
        
        <div className="col-10 view_port px-2 mx-3  ">
            <h1 className="text-white p-4" style={{ fontFamily:"Lato" ,fontWeight : "bold" , color:"#fff"}}> Ajouter un article </h1>
            <div className="col-12 d-flex align-items-center justify-content-center">
                <form onSubmit={""} className="col-7 form-group mx-auto align-items-center justify-content-center">
                    <label for="title" name="title" className="form-label p-1 mt-2">
                        Title
                    </label>
                    <input className="form-control" placeholder="Entrer le titre de l'article" id="title" name = "description" ></input>
                    <label for="title" name="title" className="form-label p-1 mt-2">
                        Description
                    </label>
                    <div className="bg-primary">
                        <Editor
                            editorState={editorState}
                            wrapperClassName="demo-wrapper"
                            editorClassName="demo-editor"
                            onEditorStateChange={onEditorStateChange}
                        />
                        <textarea
                            disabled
                            value={draftToHtml(convertToRaw(editorState.getCurrentContent()))}
                        />
                    </div>
                    <label for="title" name="title" className="form-label pr-4 mt-4">
                        Image
                    </label>
                    <input type="file" className="pt-4" placeholder="Entrer le titre de l'article" id="image" name="image" ></input>
                    <br/>
                    <button className="form-control" type="submit" className="col-4 p-2 mt-4 btn btn-primary float-right" > Enregistrer <AiFillSave/></button> 
                </form>
            </div>
        </div>
        
    )
}
